mapper.select:
select spider_id,spider_back_id, spider_name, spider_Type,create_Type,spider_Des, spider_Link,spider_default_params_all,spider_default_params, status, create_time, remark from spider_spidermanage
and spider_Type = #{spiderType} and spider_Name = #{spiderName} and status = #{status}
AND (spider_name like concat(concat('%', #{searchValue}), '%') OR spider_Type like concat(concat('%', #{searchValue}), '%'))

select spider_id,spider_back_id, spider_name,
spider_Type,create_Type,spider_Des,
spider_Link,spider_default_params_all,spider_default_params,
status,
create_time,
remark from spider_spidermanage

	status = #{status}
	 and spider_Type = #{spiderType}
	 and spider_Name = #{spiderName}

select spider_id,spider_back_id, spider_name,status,
create_time
from spider_spidermanage where spider_name=#{spiderName}

mapper.update:
update spider_spidermanage
spider_back_id=#{spiderBackId}, spider_name=#{spiderName}, spider_Type=#{spiderType}, create_Type=#{createType}, spider_Des=#{spiderDes}, spider_Link=#{spiderLink}, spider_default_params_all = #{spiderDefaultParamsAll}, spider_default_params = #{spiderDefaultParams}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where 1=1 and spider_id = #{spiderId}

update spider_spidermanage

	spider_default_params_all =
		#{spiderDefaultParamsAll},

	spider_default_params =
	#{spiderDefaultParams}

where spider_id = #{spiderId}
