mapper.select:
select task_info_id, task_id, task_name, spider_name,
task_status,finish_status,
spider_default_params_all,task_params,
start_time,
end_time,consume_time, spider_result from
spider_spidertaskinfo

	 and task_info_id = #{taskInfoId}
	 and task_id = #{taskId}
	 and task_name = #{taskName}
	 and spider_name = #{spiderName}
	 and task_status = #{taskStatus}
	 and task_params = #{taskParams}
	 and start_time = #{startTime}
	 and end_time = #{endTime}
	 and spider_result = #{spiderResult}

		AND task_info_id like concat(concat('%', #{searchValue}),
		'%')

mapper.update:
update spider_spidertaskinfo
task_id = #{taskId}, task_name = #{taskName}, spider_name = #{spiderName}, task_status = #{taskStatus}, finish_status = #{finishStatus}, spider_default_params_all = #{spiderDefaultParamsAll},
task_params = #{taskParams}, start_time = #{startTime}, end_time = #{endTime}, spider_result = #{spiderResult}
where task_info_id = #{taskInfoId}

update spider_spidertaskinfo
task_status = #{taskStatus}, finish_status = #{finishStatus}, start_time = #{startTime}, end_time =#{endTime}, consume_time=#{consumeTime}
where task_info_id = #{taskInfoId}

update spider_spidertaskinfo

	task_status = #{taskStatus},
	finish_status = #{finishStatus},
	end_time = #{endTime},
	consume_time=#{consumeTime}

where task_info_id = #{taskInfoId}
